insert into service(service_type, description, cost) VALUES ('Стрижка машинкой', 'Стрижка машинкой – это профессиональная услуга, предоставляемая опытными парикмахерами, направленная на создание современного и стильного образа. При использовании специализированных машинок и инструментов, парикмахер тщательно обрабатывает волосы, создавая точные и ровные линии стрижки. Эта услуга идеально подходит для тех, кто предпочитает короткие или средние варианты стрижки, а также для тех, кто ценит быстроту и эффективность процесса. Благодаря профессиональному подходу и внимательному отношению к деталям, стрижка машинкой обеспечивает идеальный результат, отвечающий индивидуальным предпочтениям и стилю каждого клиента.', 1000);
insert into service(service_type, description, cost) VALUES ('Стрижка ножницами', '"Стрижка ножницами" – это высокоточная и творческая услуга, предоставляемая опытными парикмахерами, которые используют свой профессионализм и мастерство для создания индивидуального и стильного образа. В ходе процедуры парикмахер внимательно анализирует структуру волос и особенности лица каждого клиента, чтобы подобрать идеальную стрижку, которая выделит его индивидуальность и подчеркнет его уникальный стиль. Используя только качественные профессиональные ножницы и инструменты, парикмахер тщательно работает над каждой прядью волос, создавая эффектные формы, объемы и текстуры. Эта услуга позволяет добиться идеального баланса между традиционной ручной работой и современными тенденциями моды, обеспечивая клиентам неповторимый и стильный образ, который будет вписываться в их уникальный образ жизни и предпочтения.', 1500);
